import { puzzleInputs } from "../../utils/PuzzleInputs"

const { Grid } = require("@mui/material")

const createBooleanSquare = (width, height) => {
    return new Array(height)
        .fill()
        .map(() => new Array(width).fill(false))
}
const sandOrigin = { x: 500, y: 0 }

const EmptyCave = ({ version }) => {
    const parsed = puzzleInputs[14][version].split("\n")
        .map(line => line
            .split(" -> ")
            .map(pos => pos
                .split(",")
                .map(n => parseInt(n))
            )
            .map(pos => ({ x: pos[0], y: pos[1] }))
        )
    const largestY = parsed.flat().map(pos => pos.y).sort((a, b) => b - a)[0]
    const cave = createBooleanSquare(sandOrigin.x * 2, largestY)
    return <Grid />
}

export default EmptyCave
