import { Box, Stack } from "@mui/material"

const Grid = ({ booleanGrid, renderTrue, renderFalse, height, width }) => {
    return <Stack
        alignItems="center"
        justifyContent="space-between"
        sx={{
            height: height,
            width: width
        }}
    >
        {booleanGrid.map((row, y) => (
            <Stack
                key={y}
                sx={{
                    height: height / booleanGrid.length,
                    width: width
                }}
            >
                {row.map((value, x) => (
                    <Box
                        key={x}
                        sx={{
                            height: height / booleanGrid.length,
                            width: width / booleanGrid[0].length,
                        }}
                    >
                        {value ? renderTrue() : renderFalse()}
                    </Box>
                ))}
            </Stack>
        ))}
    </Stack>
}

export default Grid
