import { colors } from "../../utils/CustomTheme"
import { puzzleInputs } from "../../utils/PuzzleInputs"

import { Grid } from "../Atoms"
const range = n => new Array(n)
    .fill()
    .map((v, i) => i)

const createBooleanGrid = (width, height) => {
    return new Array(height)
        .fill()
        .map(() => new Array(width).fill(false))
}
const sandOrigin = { x: 500, y: 0 }

const EmptyCave = ({ sx, version }) => {
    const parsed = puzzleInputs[14][version].split("\n")
        .map(line => line
            .split(" -> ")
            .map(pos => pos
                .split(",")
                .map(n => parseInt(n))
            )
            .map(pos => ({ x: pos[0], y: pos[1] }))
        )
    const largestY = parsed.flat().map(pos => pos.y).sort((a, b) => b - a)[0]
    const booleanCave = createBooleanGrid(sandOrigin.x * 2, largestY + 1)
    parsed.forEach(formation => {
        formation
            .slice(0, -1)
            .forEach((pos, i) => {
                const nextPos = formation[i + 1]
                const xDiff = Math.abs(pos.x - nextPos.x)
                if (xDiff > 0) {
                    range(xDiff + 1)
                        .forEach(offset => {
                            if (pos.x > nextPos.x) booleanCave[pos.y][pos.x - offset] = true
                            if (pos.x < nextPos.x) booleanCave[pos.y][pos.x + offset] = true
                        })
                }
                const yDiff = Math.abs(pos.y - nextPos.y)
                if (yDiff > 0) {
                    range(yDiff + 1)
                        .forEach(offset => {
                            if (pos.y > nextPos.y) booleanCave[pos.y - offset][pos.x] = true
                            if (pos.y < nextPos.y) booleanCave[pos.y + offset][pos.x] = true
                        })
                }
            })
    })

    return <Grid
        sx={sx}
        booleanGrid={booleanCave}
        renderTrue={() => ({ backgroundColor: colors.saffron })}
        renderFalse={() => ({})}
        height={booleanCave.length * 2}
        width={booleanCave[0].length * 2}
    />
}

export default EmptyCave
