import { TextField } from "@mui/material"
import React from "react"

const usePuzzleState = initialValue => {
    const [value, setValue] = React.useState(initialValue)
    return {
        value,
        setValue,
    }
}

const PuzzleInput = ({ sx, puzzleState: { value, setValue } }) => {
    return <TextField
        sx={{
            ...sx,
            overflowY: "auto",
        }}
        multiline
        rows={10}
        value={value}
        onChange={event => setValue(event.target.value)}
    />
}

export default PuzzleInput
export {
    usePuzzleState
}
