import { Box, Stack } from "@mui/material"

const Grid = ({ booleanGrid, renderTrue, renderFalse }) => {
    return <Stack
        alignItems="center"
        justifyContent="space-between"
        sx={{ height: booleanGrid.length, width: booleanGrid[0].length }}
    >
        {booleanGrid.map((row, y) => (
            <Stack key={y}>
                {row.map((value, x) => (
                    <Box key={x} sx={{ height: 1, width: 1, fontSize: 1 }}>
                        {value ? renderTrue() : renderFalse()}
                    </Box>
                ))}
            </Stack>
        ))}
    </Stack>
}

export default Grid
