import React from "react"
import { useNavigate, useParams } from "react-router-dom"
import {
    AppBar,
    Breadcrumbs,
    MenuItem,
    Stack,
    Typography,
} from "@mui/material"
import NavigateNextIcon from '@mui/icons-material/NavigateNext'

import { colors } from "../../utils/CustomTheme"

const pages = [
    { text: "Genscom", link: "/" },
]
const headerHeight = 70

const BreadcrumsElement = () => {
    const navigate = useNavigate()
    const { clientName, newspaperName, pageNumber, articleNumber } = useParams()
    const breadcrumbsList = [
        "Alles",
        `${clientName}`,
        `krant ${newspaperName}`,
        `pagina ${pageNumber}`,
        `artikel ${articleNumber}`,
    ].filter(value => !value.includes("undefined"))

    const breadcrumbActions = [
        () => navigate("/"),
        () => navigate(`/${clientName}/newspapers`),
        () => navigate(`/${clientName}/${newspaperName}/pages`),
        () => navigate(`/${clientName}/${newspaperName}/${pageNumber}/edit`),
        () => navigate(`/${clientName}/${newspaperName}/${pageNumber}/${articleNumber}/articleDetails`),
    ]

    return <Breadcrumbs
        separator={<NavigateNextIcon sx={{ color: colors.white }} fontSize="small" />}
        aria-label="breadcrumb"
    >
        {breadcrumbsList.map((value, index) => (
            <Typography
                key={value}
                sx={{ color: colors.white, cursor: index < breadcrumbsList.length - 1 ? "pointer" : "unset" }}
                onClick={breadcrumbActions[index]}
            >
                {value}
            </Typography>
        ))}
    </Breadcrumbs>
}

const ResponsiveAppBar = () => {
    const navigate = useNavigate()

    return (
        <AppBar
            position="fixed"
            sx={{
                height: headerHeight,
                zIndex: (theme) => theme.zIndex.drawer + 1,
                paddingRight: 5,
                paddingLeft: 5,
                backgroundImage: "unset"
            }}>
            <Stack direction="row" justifyContent="space-between" alignItems="center" columnGap={10}>
                <Stack direction="row" sx={{ height: headerHeight, width: "33%" }}>
                    {pages.map((page) => (
                        <MenuItem
                            key={page.text}
                            onClick={() => navigate(page.link)}
                            sx={
                                window.location.pathname === page.link
                                    ? {
                                        backgroundColor: colors.lightBlue,
                                        "&:hover": {
                                            backgroundColor: colors.lightBlue,
                                        }
                                    } : {}}>
                            <img src={process.env.PUBLIC_URL + '/static/genscom-logo.png'} style={{ height: headerHeight }} />
                        </MenuItem>
                    ))}
                </Stack>
                <Stack direction="row" justifyContent="center" alignItems="center" sx={{ height: headerHeight, width: "33%" }}>
                    <BreadcrumsElement />
                </Stack>
                <div style={{ width: "33%" }}></div>
            </Stack>
        </AppBar >
    )
}
export default ResponsiveAppBar
export { headerHeight }
