import { Typography } from "@mui/material"
import React from "react"
import { colors } from "../../utils/CustomTheme"
import { puzzleInputs } from "../../utils/PuzzleInputs"

import { Grid } from "../Atoms"
const range = n => new Array(n)
    .fill()
    .map((v, i) => i)

const createBooleanGrid = (width, height) => {
    return new Array(height)
        .fill()
        .map(() => new Array(width).fill(false))
}
const sandOrigin = { x: 500, y: 0 }

const EmptyCave = ({ sx, version }) => {
    const [booleanCave, setBooleanCave] = React.useState(null)
    const [loading, setLoading] = React.useState(true)
    React.useEffect(() => {
        setLoading(true)
        const parsed = puzzleInputs[14][version].split("\n")
            .map(line => line
                .split(" -> ")
                .map(pos => pos
                    .split(",")
                    .map(n => parseInt(n))
                )
                .map(pos => ({ x: pos[0], y: pos[1] }))
            )
        const largestY = parsed.flat().map(pos => pos.y).sort((a, b) => b - a)[0]
        const largestX = parsed.flat().map(pos => pos.x).sort((a, b) => b - a)[0]
        const smallestX = parsed.flat().map(pos => pos.x).sort((a, b) => a - b)[0]
        const cave = createBooleanGrid(largestX - smallestX, largestY + 1)
        parsed.forEach(formation => {
            formation
                .slice(0, -1)
                .forEach((pos, i) => {
                    const nextPos = formation[i + 1]
                    const xDiff = Math.abs(pos.x - nextPos.x)
                    if (xDiff > 0) {
                        range(xDiff + 1)
                            .forEach(offset => {
                                if (pos.x > nextPos.x) cave[pos.y][pos.x - offset] = true
                                if (pos.x < nextPos.x) cave[pos.y][pos.x + offset] = true
                            })
                    }
                    const yDiff = Math.abs(pos.y - nextPos.y)
                    if (yDiff > 0) {
                        range(yDiff + 1)
                            .forEach(offset => {
                                if (pos.y > nextPos.y) cave[pos.y - offset][pos.x] = true
                                if (pos.y < nextPos.y) cave[pos.y + offset][pos.x] = true
                            })
                    }
                })
        })
        setBooleanCave(cave)
        // setLoading(false)
    }, [version])

    if (loading) return <Typography>LOADING</Typography>

    return <Grid
        sx={sx}
        booleanGrid={booleanCave}
        renderTrue={() => ({ backgroundColor: colors.saffron })}
        renderFalse={() => ({})}
        height={booleanCave.length * 4}
        width={booleanCave[0].length * 4}
    />
}

export default EmptyCave
